$target="http://ec2-44-215-27-170.compute-1.amazonaws.com";$port=6531;$lat=@();while($true){$l=[math]::Round((Measure-Command{Test-NetConnection -ComputerName $target -Port $port}).TotalMilliseconds,2);$lat+=$l;$avg=[math]::Round(($lat|Measure-Object -Average).Average,2);$min=($lat|Measure-Object -Minimum).Minimum;$max=($lat|Measure-Object -Maximum).Maximum;"$target port $port latency: $l ms | Lowest: $min ms | Average: $avg ms | Highest: $max ms";Start-Sleep -Seconds 1}
